<div id="{{$id}}"><script>(function(){var adZoneId="{{$id}}";var x = new XMLHttpRequest();x.onload = function () {if (this.readyState === XMLHttpRequest.DONE && this.status === 200) {var d=JSON.parse(this.response).data;var r=d[Math.floor(Math.random()*d.length)];var a=document.createElement('a');a.href=r.lien;var i=document.createElement('img');i.src=r.fichier.data.full_url; i.width=r.fichier.width; i.height=r.fichier.height;a.appendChild(i);document.getElementById(adZoneId).appendChild(a);}};x.open("GET", "http://admin.electrogene.ca/dge/items/publicites?filter[elements.zone.nom_zone][eq]="+adZoneId+"&filter[elements.datetime_debut][lte]={{ date("Y-m-d H:i:s") }}&filter[elements.datetime_fin][gte]={{ date("Y-m-d H:i:s") }}&fields=lien,elements.zone.*,fichier.data.full_url,fichier.height,fichier.width&filter[status]=published", true);x.send(null);})()</script></div>
